mapper.select:
select 
from lottery_admin
where id = #{id, jdbcType=INTEGER}

mapper.delete:
delete from lottery_admin
where id in

    #{id}

mapper.select:
select 
from lottery_admin


        and id like #{id}


        and head_img like #{headImg}


        and username like #{username}


        and password like #{password}


        and create_time like #{createTime}


        and update_time like #{updateTime}
